# ===== MẢNG MERGED DUY NHẤT - CÁCH TIẾP CẬN TỐI ÂU =====
# Gộp 2 mảng thành 1 mảng duy nhất để cải thiện hiệu suất
def _create_merged_professions():
    """
    Tạo mảng merged một lần duy nhất khi module được import
    """
    merged_professions = []
    
    # Tạo dictionary để map profession name với Vietnamese keywords
    vietnamese_dict = {prof["name"]: prof["keywords"] for prof in professions_vietnamese}
    
    for english_prof in professions_english:
        profession_name = english_prof["name"]
        vietnamese_keywords = vietnamese_dict.get(profession_name, [])
        
        # Tạo profession object với tất cả keywords
        merged_profession = {
            "name": profession_name,
            "english_keywords": english_prof["keywords"],
            "vietnamese_keywords": vietnamese_keywords,
            "all_keywords": english_prof["keywords"] + vietnamese_keywords
        }
        
        merged_professions.append(merged_profession)
    
    return merged_professions

# Tạo mảng merged một lần duy nhất khi module được import
MERGED_PROFESSIONS = _create_merged_professions()

def normalize_title(title):
    """
    Normalize job title sử dụng mảng merged professions
    """
    result = normalize_title_enhanced(title, "both", 0.8)
    return result["profession"]

def normalize_title_enhanced(title, language="both", threshold=0.8):
    """
    Phiên bản nâng cao của normalize_title với nhiều tùy chọn hơn
    
    Args:
        title (str): Job title cần normalize
        language (str): "english", "vietnamese", hoặc "both"
        threshold (float): Ngưỡng score để match (0.0-1.0)
    
    Returns:
        dict: Thông tin chi tiết về match
    """
    import re
    if not title:
        return {"profession": "Unknown", "score": 0.0, "matched_keyword": None}
    
    normalized = re.sub(r'\([^)]*\)', '', title)
    normalized = re.sub(r'-.*$', '', normalized) 
    normalized = re.sub(r'[^a-zA-Zàáạảãâầấậẩẫăằắặẳẵèéẹẻẽêềếệểễìíịỉĩòóọỏõôồốộổỗơờớợởỡùúụủũưừứựửữỳýỵỷỹđ\s]', '', normalized)
    normalized = normalized.strip().lower()
    
    best_match = None
    best_score = 0.0
    best_keyword = None
    
    # Sử dụng mảng merged tĩnh thay vì tạo mới mỗi lần
    for profession in MERGED_PROFESSIONS:
        # Chọn keywords theo ngôn ngữ
        keywords_to_check = []
        if language == "english":
            keywords_to_check = profession["english_keywords"]
        elif language == "vietnamese":
            keywords_to_check = profession["vietnamese_keywords"]
        else:  # both
            keywords_to_check = profession["all_keywords"]
        
        for keyword in keywords_to_check:
            # Bỏ qua keywords quá ngắn (1-2 ký tự) để tránh false positive
            if len(keyword.strip()) <= 2:
                continue
                
            keyword_normalized = keyword.lower().strip()
            
            # Method 1: Exact substring match (highest priority)
            if keyword_normalized in normalized:
                exact_score = len(keyword_normalized) / len(normalized) * 2  # Boost exact matches
                if exact_score > best_score:
                    best_match = profession["name"]
                    best_score = exact_score
                    best_keyword = keyword
                continue
            
            # Method 2: Token-based matching với cải tiến
            keyword_tokens = keyword_normalized.split()
            if not keyword_tokens:
                continue
                
            # Chỉ tính score nếu có ít nhất 2 tokens hoặc 1 token dài >= 4 ký tự
            if len(keyword_tokens) == 1 and len(keyword_tokens[0]) < 4:
                continue
                
            normalized_tokens = normalized.split()
            match_count = 0
            
            for token in keyword_tokens:
                # Exact token match
                if token in normalized_tokens:
                    match_count += 1
                # Partial token match (cho phép match một phần của từ dài)
                elif len(token) >= 4:
                    for norm_token in normalized_tokens:
                        if token in norm_token or norm_token in token:
                            match_count += 0.7  # Partial match score
                            break
            
            score = match_count / len(keyword_tokens)
            
            # Boost score cho keywords có nhiều tokens match
            if len(keyword_tokens) > 1 and match_count >= 2:
                score *= 1.2
                
            if score >= threshold and score > best_score:
                best_match = profession["name"]
                best_score = score
                best_keyword = keyword
    
    return {
        "profession": best_match if best_match else "Unknown",
        "score": min(best_score, 1.0),  # Cap score at 1.0
        "matched_keyword": best_keyword,
        "original_title": title
    }
